package com.example.kotlin3

import android.content.DialogInterface
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.LayoutInflater
import androidx.appcompat.app.AlertDialog
import com.example.kotlin3.databinding.ActivityMainBinding
import java.nio.file.Files.delete

class
MainActivity : BaseActivity<ActivityMainBinding>() {

   private var adapter : CharacterAdapter? = null

    private var data = arrayListOf<Character>()



    override fun initView() {
        loadData()
        adapter = CharacterAdapter(data = data,
            onLongItemClick = this::onLongItemClick)
        binding.recyclerView.adapter = adapter
    }

   private fun onLongItemClick(pos:Int){
       val alertDialog = AlertDialog.Builder(this)
       alertDialog.setTitle(getString(R.string.delete))
       alertDialog.setPositiveButton(getString(R.string.yes)
       ) { d, _ ->
           deleteItem(pos)

           d.dismiss()
       }
       alertDialog.setNegativeButton(getString(R.string.no)){ d, _ ->
           d.dismiss()
       }
        alertDialog.show()
   }

    private fun deleteItem(pos: Int) {
        adapter?.removeItem(pos)
    }


